import React from "react";
import "../../App.css";

export default function ListItem({ text, check }) {
  const [res, setRes] = React.useState(true);
  const [underline, setUnderline] = React.useState(false);
  return (
    <div className={`${res ? "" : "hide"} listItem`}>
      <input
        type="checkbox"
        onChange={(event) => setUnderline(event.target.checked)}
      />
      <span style={{ textDecoration: underline ? "line-through" : "" }}>
        {text}
      </span>
      <button className="vAlign" onClick={() => setRes(false)}>
        x
      </button>
    </div>
  );
}
