import React from "react";
import "../../App.css";

export default function ListItem({ text, check }) {
  const [hidden, setHidden] = React.useState(false);
  const [strike, setStrike] = React.useState(check);
  return (
    <div className={`${hidden ? "hide" : ""} listItem`}>
      <input
        type="checkbox"
        defaultChecked={check ? "checked" : ""}
        onChange={(event) => {
          setStrike(event.target.checked);
        }}
      />
      <span className={`${strike ? "strike" : ""}`}>{text}</span>
      <button className="vAlign" onClick={() => setHidden(true)}>
        x
      </button>
    </div>
  );
}
