import React from 'react'
import '../../App.css';

export default function ListItem({ text, check }) {
	const [res, setRes] = React.useState(true);
	let spanElem = React.createRef();
	return (
		<div className={`${(res ? "":"hide")} listItem`}>
			<input type="checkbox" checked={check} onClick={spanElem.style.textDecoration="underline"}/>
			<span className={`${this.previousElementSibling}`} ref={spanElem}>{text}</span>
				<button style={{ marginTop: "auto", marginBottom: "auto" }} onClick={() => setRes(false)}>x</button>
			</div>
		
	)
}
