import React from 'react'
import '../../App.css';

export default function ListItem({ text, check }) {
	const [res, setRes] = React.useState(true);
	const [underline, setUnderline] = React.useState(true);
	return (
		<div className={`${(res ? "":"hide")} listItem`}>
			<input type="checkbox" onClick={() => setUnderline(!underline)} checked/>
			<span style={{textDecoration:(underline ? "":"line-through")}}>{text}</span>
			<button style={{ marginTop: "auto", marginBottom: "auto" }} onClick={() => setRes(false)}>x</button>
		</div>
		
	)
}
