import React from "react";
import "../../App.css";

export default function ListItem({ text, check, deleteHandler }) {
  const [strike, setStrike] = React.useState(false);
  return (
    <div>
      <input
        type="checkbox"
        defaultChecked={check ? "checked" : ""}
        onChange={(event) => {
          setStrike(event.target.checked);
        }}
        style={{ display: "inline" }}
      />
      {strike ? (
        <strike style={{ display: "inline" }}>{text}</strike>
      ) : (
        <p style={{ display: "inline" }}>{text}</p>
      )}
      <button onClick={deleteHandler} style={{ display: "inline" }}>
        x
      </button>
    </div>
  );
}
