import React from 'react'

export default function ListItem({ text, check }) {
	const [res, setRes] = React.useState(true);
	const clickHandler = () => setRes(false);
	return (
		<div style={{ display: "flex", alignItems: "center", justifyContent:"center"}}>
			<input type="checkbox" checked={check} />
			<span>{text}</span>
			{res ? <button style={{ marginTop: "auto", marginBottom: "auto" }} onClick={ clickHandler }>x</button> : null}
		</div>
	)
}
